import { useState, useEffect } from "react";
import moviesBL from './moviesUtils'
import { Link} from 'react-router-dom'


function MovieComp(props) {

  const [movID , setMovId] = useState(props.movId);
  const [movie, setMovie] = useState({});
  const [movGen , setMovGen] = useState([]);
  useEffect(async () => {
  let result = await moviesBL.getMovie(movID);
  setMovie(result.data)
  setMovGen(result.data.genres)
  }, [])

  const goToEdit = ()=>
  {
    props.history.push('/edit/' + props.movId)
  } 
  const styles = {
    border: '3px solid black', 
    margin: '2px',
    width: '400px'
};
  return (
    <div className="App" style={styles}>
      <h3>{movie.name}, {movie.premiered}</h3>
      Genres:
      <ul>
      {movGen.map((gen, index)=> 
      {
        return <li key={index}>{gen}</li>
      }) }
        </ul> 

        <img src={movie.image} alt={movie.name} />
        <Link to={"/edit/" + movID}>Edit</Link>       {/* <button onClick={goToEdit}>Edit Movie</button> */}
    </div>
  );
}

export default MovieComp;
