import axios from 'axios';


const isAllowedToLogin = async (usrName, pwd)=> 
{
  let result = await axios.get("http://localhost:8000/api/users");
  let allUsers = result.data;

  allUsers.forEach(usr => {
    if(usr.username == usrName && usr.password == pwd)
    {
      let isAllowed = {allowedToLogin : true, fullName : usr.fullName}
      return isAllowed;
    }
    else
    {
      return { allowedToLogin : false};
    }

  });
}

export default {isAllowedToLogin};
