import { useState, useEffect, memo } from "react";
import {withRouter} from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import subsBL from './subscriptionUtils';
import memBL from '../members/membersUtils';


const useStyles = makeStyles({
  root: {
    maxWidth: 345,
    margin: 10

  },

});

function SubPerMovieComp(props) {

  const classes = useStyles();

  const [movID] = useState(props.movId);
  const [subs, setSubs] = useState([]);
  const [mems, SetMems] = useState([]);

  useEffect( () => {
  async function fetchData() {
  let result = await subsBL.getSubsPerMovieID(movID);
  setSubs(result)

  let resp = await memBL.getAllMembers();
  let members = resp.data;
  members.forEach(mem => {
    if(subs.member_id === mem._id)
    {
      mems.push(mem.name);
    }

  });

    }
    fetchData();
  }, [movID])



  return (

    <Card className={classes.root}>
    <CardActionArea>
      <CardContent>
        <Typography gutterBottom variant="h5" component="h2">
        Subscription Watched:
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
        <ul>
          {
            subs.map(sub =>
              {
                return <li key={sub._id}>{sub.date}</li>
              })
          }
        </ul>


        </Typography>
      </CardContent>
    </CardActionArea>
    <CardActions>

    </CardActions>
  </Card>


  );
}

export default withRouter(SubPerMovieComp) ;
