import { useState, useEffect } from "react";
import {withRouter} from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {Link} from 'react-router-dom';
import subsBL from '../utils/subscriptionUtils';


const useStyles = makeStyles({
  root: {
    maxWidth: 345,
    margin: 10

  },

});

function SubPerMemberComp(props) {

  const classes = useStyles();

  const [memID] = useState(props.memId);
  const [subs, setSubs] = useState([]);
  const [isSubToNewMovieVisible, setIsSubToNewMovieVisible] = useState(false);


  useEffect(() => {
    let isMounted = true; 
    async function fetchData() {
      let result = await subsBL.getSubsPerMemberID(memID);
      if(isMounted){
        setSubs(result);
      }
    }
    fetchData();
    return  () => { isMounted = false };

  }, [])

  return (

    <Card className={classes.root}>
    <CardActionArea>
      <CardContent>
        <Typography gutterBottom variant="h5" component="h2">
      Movies Watched:
        </Typography>
       <button variant="contained" size="small" onClick={()=> setIsSubToNewMovieVisible(!isSubToNewMovieVisible)}>Subscribe To a New Movie</button>
        <ul>
          {
            subs.map((sub, idx) =>
              {
                return <li key={idx}> <Link to={"/main/movies/edit-movie/" + sub.movId}>{sub.movName}</Link>&nbsp;
                  {sub.date}</li>
              })
          }
        </ul>


      </CardContent>
    </CardActionArea>
    <CardActions>

    </CardActions>
  </Card>


  );
}

export default withRouter(SubPerMemberComp) ;
