import './App.css';
import {Switch, Route, Link} from 'react-router-dom'
import MainComp from './MainPage';
import userBL from './userUtils';

function LoginComp() {
  const [userName , setUserName] = useState('');
  const [userPwd , setUserPwd] = useState('');

  const checkLogin = (e) => 
  {
    e.preventDefault();
    let isAllowed = userBL.isAllowedToLogin(userName, userPwd);
    console.log(isAllowed);
  }
  return (
    <div className="App">
 <h1>Movies Subscriptions Web Site !!</h1>
<form onSubmit={e => checkLogin(e)}>
  User Name: <input type="text" onChange={e => setUserName(e.target.value)} /> <br/>
  Password: <input type="text" onChange={e => setUserPwd(e.target.value)} /> <br/>
    <input type="submit" value="Login" />
</form>


<Switch>
  <Route path="/main" component={MainComp} />

</Switch>
    </div>
  );
}

export default LoginComp;
