import axios from 'axios';

const getAllMovies = ()=> 
{
    return axios.get("http://localhost:8000/api/movies");
}

const getMovie = (id)=> 
{
    return axios.get("http://localhost:8000/api/movies/" + id);
}



export default {getAllMovies, getMovie};
