  useEffect(async () => {
  let result = await moviesBL.getMovie(movID);
  setMovie(result.data)
  }, [])



  return (
    <div className="App">
      <h3>Edit Movie {sessionStorage["fullName"]}</h3>

    </div>
  );
}

export default EditMovieComp;
