import { useState , useEffect } from "react";
import MovieComp from "./MovieComp";
import moviesBL from './moviesUtils'
import Button from '@material-ui/core/Button';



function AllMoviesComp(props){

  const [allMovies, SetAllMovies] = useState([]);



   useEffect( () => {
    async function fetchData() {
      let result = await moviesBL.getAllMovies();
       SetAllMovies(result.data);
    }
    fetchData();

}, [])


  const goToAddMovie = () => {
    props.history.push('/main/add-movie')
  }



  return (
    <div className="App">
      <h3>Movies page for {sessionStorage["fullName"]}</h3>

      <nav>
      <Button variant="contained">All Movies</Button> &nbsp;

        <Button variant="contained" onClick={goToAddMovie}>Add Movie</Button>
      </nav>

      {
        allMovies.map(mov => 
          {
            return <MovieComp key={mov._id} movId={mov._id}></MovieComp>
          })
      }


  <Switch>
      <Route path="/main/movies" component={AllMoviesComp} /> 
      <Route path="/main/edit-movie/:id" component={EditMovieComp} /> 
      <Route path="/main/add-movie" component={AddMovieComp} /> 
    </Switch>

    </div>
  );
}

export default AllMoviesComp;
