import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import subsBL from './subscriptionUtils';
import memBL from '../members/membersUtils';


const useStyles = makeStyles({
  root: {
    maxWidth: 345,
    margin: 10

  },

});

function SubPerMovieComp(props) {

  const classes = useStyles();

  const [movID] = useState(props.movId);
  const [subs, setSubs] = useState([]);
  const [mems, SetMems] = useState('');

  useEffect( () => {
  async function fetchData() {
  let result = await subsBL.getSubsPerMovieID(movID);
  setSubs(result)
  console.log(subs)

  // let resp = await memBL.getMember(subs[0].member_id);
  // let member = resp.data;
  //   console.log(member)

    }
    fetchData();
  }, [movID])



  return (

    <Card className={classes.root}>
    <CardActionArea>
      <CardContent>
        <Typography gutterBottom variant="h5" component="h2">
        Subscription Watched:
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
        <ul>
          {
            subs.map(sub =>
              {
                return <li key={sub._id}> {mems}
                  {sub.date}</li>
              })
          }
        </ul>


        </Typography>
      </CardContent>
    </CardActionArea>
    <CardActions>

    </CardActions>
  </Card>


  );
}

export default withRouter(SubPerMovieComp) ;
