import { useState , useEffect } from "react";
import MovieComp from "./MovieComp";
import moviesBL from './moviesUtils'
import Button from '@material-ui/core/Button';
import {Switch, Route} from 'react-router-dom'
import EditMovieComp from './EditMovie'
import AddMovieComp from './AddMovie';


function AllMoviesComp(props){

  const [allMovies, SetAllMovies] = useState([]);



   useEffect( () => {
    async function fetchData() {
      let result = await moviesBL.getAllMovies();
       SetAllMovies(result.data);
    }
    fetchData();

}, [])


  const goToAddMovie = () => {
    props.history.push('/main/movies/add-movie')
  }



  return (
    <div className="App">
      <h3>Movies page for {sessionStorage["fullName"]}</h3>

      {
        allMovies.map(mov => 
          {
            return <MovieComp key={mov._id} movId={mov._id}></MovieComp>
          })
      }

    </div>
  );
}

export default AllMoviesComp;
